var GeroLoader = {
  init: function (e) {
    var o = new XMLHttpRequest();
    (o.onreadystatechange = function () {
      if (4 === this.readyState && 200 === this.status) {
        var t = JSON.parse(o.responseText),
          n = document.createElement('script');
        (n.src = t.data.gero_url),
          (n.onload = function () {
            console.info('[INFO] - Loaded Gero Version: ' + t.data.gero_url),
              Gero.init({ domain: e.domain, gero_id: e.gero_id });
          }),
          document.head.appendChild(n);
      }
    }),
      o.open(
        'GET',
        'https://person.cellit.io/v1/gero?r=' + Date.now().toString(),
        !0
      ),
      o.send();
  },
};
